var switchBox = "switchBox-1";
var container = document.createElement("div");
container.classList.add("switchContainer");
container.innerHTML = "<div class='switchIndicator'></div>";
document.getElementById(switchBox).parentNode.insertBefore(container, document.getElementById(switchBox).nexSibling);
var box = document.getElementById(switchBox).parentNode.lastChild;
box.onclick = function(){
	if(document.getElementById(switchBox).checked){
		document.getElementById(switchBox).checked = false;
		box.classList.remove("active");
	}else{
		document.getElementById(switchBox).checked = true;
		box.classList.add("active");
	}
};
